// Funci칩n para generar un data URI de imagen placeholder
export const getPlaceholderImage = (width: number = 600, height: number = 300, text: string = 'Sin imagen'): string => {
  // Crear un canvas para generar la imagen
  const canvas = document.createElement('canvas');
  canvas.width = width;
  canvas.height = height;
  const ctx = canvas.getContext('2d');
  
  if (!ctx) {
    // Fallback a un data URI simple si no hay contexto
    return 'data:image/svg+xml;base64,';
  }
  
  // Fondo gris oscuro
  ctx.fillStyle = '#1f2937';
  ctx.fillRect(0, 0, width, height);
  
  // Texto centrado
  ctx.fillStyle = '#9ca3af';
  ctx.font = `${Math.min(width / 15, 24)}px Arial, sans-serif`;
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.fillText(text, width / 2, height / 2);
  
  return canvas.toDataURL('image/png');
};

// Funci칩n para obtener una imagen placeholder pre-generada (m치s eficiente)
export const getDefaultPlaceholder = (): string => {
  // Data URI de una imagen placeholder simple (SVG)
  return 'data:image/svg+xml;base64,';
};

// Constante para placeholder de diferentes tama침os
export const PLACEHOLDER_IMAGES = {
  default: 'data:image/svg+xml;base64,',
  small: 'data:image/svg+xml;base64,',
  medium: 'data:image/svg+xml;base64,',
  large: 'data:image/svg+xml;base64,'
};

